sys_variable_value.value:
(function(outputs, steps, stepResult, assertEqual)
{
	var test = new x_snc_devtools.TestAPI(outputs, steps, stepResult, assertEqual);
	
	test.SetResult(true);

	function TestCase_MaintainGroup(vGroup, aRoles, values, vExpected)
	{
		function TestCase_MaintainGroup_CheckResult(vInput,vExpected,vResult)
		{
			if (vExpected == false)
			{
				return (vExpected == vResult);
			}
			if (IsValidSysId(vResult) == false)
			{
				return false;
			}
			var strGroupSysId = vResult;
			var grGroupRoles = new GlideRecord('sys_group_has_role');
			grGroupRoles.addQuery('group','=',strGroupSysId);
			grGroupRoles.query();
			while (grGroupRoles.next())
			{
				var grRole = GetRoleRecord(grGroupRoles.role);
				if (IsValidRecord(grRole))
				{
					var strRoleName = GetStringValue(grRole.name);
					if (ArrayValueExists(aRoles,strRoleName) == false) 
					{
						test.Log('Unexpected group found!');
						return false;
					}
				}
			}
			aRoles = GetArrayValue(aRoles);
			for (var nRole = 0; nRole < aRoles.length; nRole++)
			{
				var grRole = GetRoleRecord(aRoles[nRole]);
				if (IsValidRecord(grRole) == false)
				{
					test.Log('Invalid role specified!');
					return false;
				}
				var grGroupRole = new GlideRecord('sys_group_has_role');
				grGroupRole.addQuery('group','=',strGroupSysId);
				grGroupRole.addQuery('role','=',grRole.sys_id);
				grGroupRole.query();
				if (grGroupRole.next() != true)
				{
					test.Log('Expected role is not assigned to group!');
					return false;
				}
			}

			return true;
		}

		test.TestCase(
			'Function MaintainGroup()',
			MaintainGroup,
			{
				'vGroup': vGroup,
				'aRoles': aRoles,
				'values': values
			},
			vExpected,
			TestCase_MaintainGroup_CheckResult);
	}

	TestCase_MaintainGroup(null,[],null,false);
	TestCase_MaintainGroup('TEST Service Desk',['itil'],null,true);

	grGroup = MaintainGroup('TEST Admins',['admin']);
	TestCase_MaintainGroup('TEST Admins',['admin'],null,true);
	TestCase_MaintainGroup('TEST Admins',['security_admin'],null,true);
	TestCase_MaintainGroup(grGroup,['security_admin'],null,true);

	
	return test.GetResult();
	
})(outputs, steps, stepResult, assertEqual);
