sys_variable_value.value:
(function(outputs, steps, stepResult, assertEqual)
{
	var test = new x_snc_devtools.TestAPI(outputs, steps, stepResult, assertEqual);
	
	test.SetResult(true);

	function TestCase_SetAppInfo(vApp,strMaintainer,strMaintainerEmail,strDocumentationUrl,strRepoUrl,vExpected)
	{
		function TestCase_SetAppInfo_CheckResult(vInput,vExpected,vResult)
		{
			if (vExpected != vResult)
			{
				return false;
			}
			if (vExpected == true)
			{
				var vApp = vInput['vApp'];
				var strMaintainer = GetStringValue(vInput['strMaintainer']);
				var strRepoUrl = vInput['strRepoUrl'];
				var grApp = new GlideRecord('sys_app');
				grApp.addQuery('sys_id','=',GetAppRecord(vApp).sys_id);
				if (strMaintainer != '')
				{
					grApp.addQuery('x_snc_devtools_maintainer','=',strMaintainer);
				}				
				if (IsValidUrl(strRepoUrl) == true)
				{
					grApp.addQuery('x_snc_devtools_repo_url','=',strRepoUrl);
				}
				grApp.query();
				if (grApp.getRowCount() != 1)
				{
					return false;
				}		
				if (IsValidUrl(strRepoUrl) == true)
				{

					var grRepo = new GlideRecord('sys_repo_config');
					grRepo.addQuery('sys_app','=',GetAppRecord(vApp).sys_id);
					grRepo.query();
					if (grRepo.getRowCount() == 1)
					{
						var grRepo = new GlideRecord('sys_repo_config');
						grRepo.addQuery('sys_app','=',GetAppRecord(vApp).sys_id);
						grRepo.addQuery('url','=',strRepoUrl);
						grRepo.query();
						if (grRepo.getRowCount() != 1)
						{
							return false;
						}
					}

					
					var grDependency = new GlideRecord('x_snc_devtools_dependency');
					grDependency.addQuery('dependency','=',GetAppRecord(vApp).sys_id);
					grDependency.addQuery('repo_url','!=',strRepoUrl);
					grDependency.query();
					if (grDependency.getRowCount() != 0)
					{
						return false;
					}
				}					
			}
			return true;
		}

		test.TestCase(
			'Function SetAppInfo()',
			SetAppInfo,
			{
				'vApp': vApp,
				'strMaintainer': strMaintainer,
				'strMaintainerEmail': strMaintainerEmail,
				'strDocumentationUrl': strDocumentationUrl,
				'strRepoUrl': strRepoUrl,
			},
			vExpected,
			TestCase_SetAppInfo_CheckResult);
	}

	TestCase_SetAppInfo(null,null,null,null,null,false);
	TestCase_SetAppInfo('x_snc_devtools',null,null,null,null,true);
	TestCase_SetAppInfo('x_snc_devtools','Batman','batman@gotham.gov',null,null,true);
	TestCase_SetAppInfo('x_snc_devtools','Batman','batmanATgotham.gov',null,null,false);
	TestCase_SetAppInfo('x_snc_devtools',null,null,null,'not_a_valid_url',false);
	TestCase_SetAppInfo('x_snc_devtools',null,null,null,'https://github.com/example/example.git',true);
	TestCase_SetAppInfo('x_snc_devtools',null,null,null,'https://github.com/saschawildgrube/servicenow-devtools.git',true);
		
	return test.GetResult();
	
})(outputs, steps, stepResult, assertEqual);
