sys_variable_value.value:
(function(outputs, steps, stepResult, assertEqual)
{
	var test = new x_snc_devtools.TestAPI(outputs, steps, stepResult, assertEqual);
	
	test.SetResult(true);

	function TestCase_MakeTestScriptForRecord(grRecord, vExpected)
	{
		function TestCase_MakeTestScriptForRecord_CheckResult(vInput,vExpected,vResult)
		{
			if (vExpected == false)
			{
				return (vExpected == vResult);
			}
			var strResult = GetStringValue(vResult);
			if (strResult.indexOf(vExpected) == -1)
			{
				return false;
			}
			return true;
		}

		test.TestCase(
			'Function MakeTestScriptForRecord()',
			MakeTestScriptForRecord,
			{
				'grRecord': grRecord
			},
			vExpected,
			TestCase_MakeTestScriptForRecord_CheckResult);
	}

	TestCase_MakeTestScriptForRecord(null,false);

	var grScriptInclude = GetRecord('sys_script_include','f8cc83189391e610959b7fda2bba103e');
	TestCase_MakeTestScriptForRecord(grScriptInclude,'TestAPI');
	TestCase_MakeTestScriptForRecord(grScriptInclude,'TestCase_MakeTestScriptForRecord(grRecord,vExpected)');

	var testdata = new TestDataAPI('DevTools');
	var strTestUser = testdata.CreateUser('Test1');
	var grUser = GetUserRecord(strTestUser);
	TestCase_MakeTestScriptForRecord(grUser,false);

	
	return test.GetResult();
	
})(outputs, steps, stepResult, assertEqual);
