sys_variable_value.value:
(function(outputs, steps, stepResult, assertEqual)
{
	var test = new x_snc_devtools.TestAPI(outputs, steps, stepResult, assertEqual);
	
	test.SetResult(true);
	
	function TestCase_AppBuilderPlatform(vApp,vExpected)
	{
		function TestCase_AppBuilderPlatform_CheckResult(vInput,vExpected,vResult)
		{
			if (vExpected == 0)
			{
				return (vExpected == vResult);
			}
			if (IsValidRecord(vResult) == false)
			{
				return false;
			}
			var grMetadata = new GlideRecord('sys_metadata');
			grMetadata.addQuery('sys_scope','=',vResult.sys_id);
			grMetadata.query();
			while (grMetadata.next())
			{
				test.Log(RenderRecordSummary(grMetadata));
			}
			var nAppFileCount = grMetadata.getRowCount();
			test.Log('Total number of application files: ' + nAppFileCount);
			if (nAppFileCount < vExpected)
			{
				return false;
			}
			return true;
		}
		
		test.TestCase(
			'Function AppBuilderPlatform()',
			AppBuilderPlatform,
			{
				'vApp': vApp
			},
			vExpected,
			TestCase_AppBuilderPlatform_CheckResult);
	}
		
	TestCase_AppBuilderPlatform(null,false);

	var grTestApp = AppCreate('TEST App','scoped','test_app');
	TestCase_AppBuilderPlatform(grTestApp,false);	

	var grTestApp = AppCreate('TEST Platform','scoped','test_platform');
	TestCase_AppBuilderPlatform(grTestApp,36);	


	return test.GetResult();
	
})(outputs, steps, stepResult, assertEqual);
