(function(outputs, steps, stepResult, assertEqual)
{
	var test = new x_snc_devtools.TestAPI(outputs, steps, stepResult, assertEqual);
	
	test.SetResult(true);

	function TestCase_ConfigurationItemServiceNowAppInsertOrUpdateFromList(
		strInstanceName, aApps, vExpected)
	{
		function TestCase_ConfigurationItemServiceNowAppInsertOrUpdateFromList_CheckResult(
			vInput,vExpected,vResult)
		{
			if (vExpected != false)
			{
				var aResults = [];
				var grCI = new GlideRecord('x_snc_devtools_cmdb_ci_appl_servicenow');
				grCI.addQuery('name','STARTSWITH','_TEST');
				grCI.orderBy('name');
				grCI.query();
				while (grCI.next())
				{
					var grRel = new GlideRecord('cmdb_rel_ci');
					grRel.addQuery('parent','=',grCI.sys_id);
					grRel.query();
					aResults.push({
							name: GetStringValue(grCI.name),
							dependency_count: GetIntegerValue(grRel.getRowCount())			
						});					
				}
				test.Log('Found in the CMDB:');
				test.Log(aResults);	

			
				if (vResult == false)
				{
					test.Log('Function did not return true.');
					return false;
				}

				var aExpected = GetArrayValue(vExpected);
				for (var nExpected = 0; nExpected < aExpected.length; nExpected++)
				{
					if (ArrayValueExists(aResults,aExpected[nExpected]) == false)
					{
						test.Log('Expected result object not found.');
						return false;
					}
				}

				return true;
			}
			return (vExpected == vResult);
		}

		test.TestCase(
			'Function ConfigurationItemServiceNowAppInsertOrUpdateFromList()',
			ConfigurationItemServiceNowAppInsertOrUpdateFromList,
			{
				'strInstanceName': strInstanceName,
				'aApps': aApps,
			},
			vExpected,
			TestCase_ConfigurationItemServiceNowAppInsertOrUpdateFromList_CheckResult);
	}

	TestCase_ConfigurationItemServiceNowAppInsertOrUpdateFromList(null,null,false);
	TestCase_ConfigurationItemServiceNowAppInsertOrUpdateFromList('testinstance',null,false);
	TestCase_ConfigurationItemServiceNowAppInsertOrUpdateFromList('testinstance',[],false);
	TestCase_ConfigurationItemServiceNowAppInsertOrUpdateFromList('testinstance',{},false);

	var strBetaSysId = GetPseudoSysId(true);
	var strGammaSysId = GetPseudoSysId(true);

	var aApps = [
		{
			sys_id: GetPseudoSysId(true),
			name: '_TEST Alpha',
			scope: 'x_test_alpha',
			version: '2.0.0',
			x_snc_devtools_repo_url: 'https://example.com/test/alpha.git',
			branch: '2.0.0',
			_dependencies: [
				{
					dependency: strBetaSysId
				},
				{
					dependency: strGammaSysId
				}
			]
		},
		{
			sys_id: strBetaSysId,
			name: '_TEST Beta',
			scope: 'x_test_beta',
			version: '1.1.0',
			x_snc_devtools_repo_url: 'https://example.com/test/beta.git',
			branch: 'master',
		},
		{
			sys_id: strGammaSysId,
			name: '_TEST Gamma',
			scope: 'x_test_gamma',
			version: '3.5.0',
			x_snc_devtools_repo_url: 'https://example.com/test/gamma.git',
			branch: 'branch01',
			
		}
	];
		
	TestCase_ConfigurationItemServiceNowAppInsertOrUpdateFromList('testinstance',aApps,
		[
			{name: '_TEST Alpha@testinstance', dependency_count: 2},
			{name: '_TEST Beta@testinstance', dependency_count: 0},
			{name: '_TEST Gamma@testinstance', dependency_count: 0}
		]);
	TestCase_ConfigurationItemServiceNowAppInsertOrUpdateFromList('',aApps,
		[
			{name: '_TEST Alpha@https://example.com/test/alpha.git@2.0.0', dependency_count: 2},
			{name: '_TEST Beta@https://example.com/test/beta.git@master', dependency_count: 0},
			{name: '_TEST Gamma@https://example.com/test/gamma.git@branch01', dependency_count: 0}
		]);
		
	return test.GetResult();
	
})(outputs, steps, stepResult, assertEqual);
