sys_variable_value.value:
(function(outputs, steps, stepResult, assertEqual)
{
	var test = new x_snc_devtools.TestAPI(outputs, steps, stepResult, assertEqual);
	
	test.SetResult(true);
	
	function TestCase_IsCodeRecord(vRecordOrSysId,vExpected)
	{
		function TestCase_IsCodeRecord_CheckResult(vInput,vExpected,vResult)
		{
			return (vExpected == vResult);
		}
		
		test.TestCase(
			'Function IsCodeRecord()',
			IsCodeRecord,
			{
				'vRecordOrSysId': vRecordOrSysId
			},
			vExpected,
			TestCase_IsCodeRecord_CheckResult);
	}
	
	TestCase_IsCodeRecord(null,false);

	var grIncident = new GlideRecord('incident');
	grIncident.initialize();
	var strIncidentSysId = grIncident.insert();
	TestCase_IsCodeRecord(strIncidentSysId,false);
	TestCase_IsCodeRecord(grIncident,false);

	var grScriptInclude = GetRecord('sys_script_include','a78b43889323ae10959b7fda2bba10bd');
	TestCase_IsCodeRecord(grScriptInclude.sys_id,true);
	TestCase_IsCodeRecord(grScriptInclude,true);

	var grApp = GetAppRecord('x_snc_devtools');
	TestCase_IsCodeRecord(grApp.sys_id,true);
	TestCase_IsCodeRecord(grApp,true);


	var grPackage = new GlideRecord('x_snc_devtools_transporter_package');	
	grPackage.initialize();
	grPackage.name = 'Test DevTools - IsCodeRecord';
	grPackage.id_suffix = 'test_devtool_iscoderecord';
	grApp = GetAppRecord('x_snc_devtools');
	grPackage.sys_scope = grApp.sys_id;
	var strPackageSysId = grPackage.insert();
	test.Log(grPackage);

	var grTest = new GlideRecord('x_snc_devtools_test');
	grTest.initialize();
	grTest.name = 'Test DevTools';
	grTest.value = 'Hello World';
	var strTestSysId = grTest.insert();
	test.Log(grTest);

	TestCase_IsCodeRecord(grTest.sys_id,false);
	TestCase_IsCodeRecord(grTest,false);

	RecordTransporterRecordPack(grPackage,grTest);

	TestCase_IsCodeRecord(grTest.sys_id,true);
	TestCase_IsCodeRecord(grTest,true);	


	return test.GetResult();
	
})(outputs, steps, stepResult, assertEqual);
