sys_variable_value.value:
(function(outputs, steps, stepResult, assertEqual)
{
	var test = new x_snc_devtools.TestAPI(outputs, steps, stepResult, assertEqual);
	
	test.SetResult(true);

	function TestCase_SetTestStepScript(vTestStep, strScript, vExpected)
	{
		function TestCase_SetTestStepScript_CheckResult(vInput,vExpected,vResult)
		{
			if (vExpected != vResult)
			{
				return false;
			}

			if (vExpected == true)
			{
				if (GetTestStepScript(vInput.vTestStep) != vInput.strScript)
				{
					return false;
				}
			}
			return true;
		}

		test.TestCase(
			'Function SetTestStepScript()',
			SetTestStepScript,
			{
				'vTestStep': vTestStep,
				'strScript': strScript
			},
			vExpected,
			TestCase_SetTestStepScript_CheckResult);
	}

	TestCase_SetTestStepScript(null,null,false);

	var grTest = new GlideRecord('sys_atf_test');
	grTest.initialize();
	grTest.name = 'TEST1';
	var strTestSysId = GlideRecordInsert(grTest);

	TestCase_SetTestStepScript(grTest,'gs.info("Hello");',false);

	var grTestStep = new GlideRecord('sys_atf_step');
	grTestStep.initialize();
	grTestStep.step_config = AppGetProperty('atf.step_config.runserversidescript');
	grTestStep.test = strTestSysId;
	var strTestStepSysId = GlideRecordInsert(grTestStep);

	TestCase_SetTestStepScript(grTest,'gs.info("Hello 1");',true);
	TestCase_SetTestStepScript(grTestStep,'gs.info("Hello 2");',true);


	return test.GetResult();
	
})(outputs, steps, stepResult, assertEqual);
