sys_script_include.script:
function RecordTransporterAutomaticUnpack(vApp,bPreviewAndCommitUpdateSets)
{
	var grApp = GetAppRecord(vApp);
	if (IsValidRecord(grApp) == false)
	{
		return false;
	}

	var bSuccess = true;

	var grPackages = new GlideRecord('x_snc_devtools_transporter_package');
	grPackages.addQuery('sys_scope','=',grApp.sys_id);
	grPackages.addQuery('auto_unpack','=','true');
	grPackages.orderBy('order');
	grPackages.query();
	while (grPackages.next())
	{
		var bPreviewAndCommitUpdateSet =
			bPreviewAndCommitUpdateSets
			&&
			GetBoolValue(grPackages.auto_commit);
		var bResult = RecordTransporterPackageUnpack(grPackages,bPreviewAndCommitUpdateSet);
		if (bResult == false)
		{
			bSuccess = false;
		}
	}

	return bSuccess;
}
