sys_script_include.script:
function AppBuilderSyncScriptIncludeToUiScript(vScriptInclude,vUiScript)
{
	var grScriptInclude = GetRecord('sys_script_include',vScriptInclude);
	if (IsValidRecord(grScriptInclude) == false)
	{
		return false;
	}
	var grUiScript = GetRecord('sys_ui_script',vUiScript);
	if (IsValidRecord(grUiScript) == false)
	{
		return false;
	}
	if (grUiScript.global == true)
	{
		//grUiScript.global = false;
		grUiScript.x_snc_devtools_global = true;
	}

	grUiScript.script_name = grScriptInclude.name;
	grUiScript.description = grScriptInclude.description;
	grUiScript.script = grScriptInclude.script;
	var strUiScriptSysId = GlideRecordUpdate(grUiScript,false);
	return IsValidSysId(strUiScriptSysId);
}
