sys_script_include.script:
function GetFormSectionRecord(vViewOrSection,strTable,strCaption)
{
	var grFormSection = GetRecord('sys_ui_section',vViewOrSection);
	if (IsValidRecord(grFormSection) == true)
	{
		return grFormSection;
	}

	var grView = GetViewRecord(vViewOrSection);
	if (IsValidRecord(grView) == false)
	{
		return false;
	}
	var grFormSection = new GlideRecord('sys_ui_section');
	grFormSection.addQuery('view','=',grView.sys_id);
	grFormSection.addQuery('name','=',strTable);
	grFormSection.addQuery('caption','=',strCaption);
	grFormSection.query();
	if (grFormSection.getRowCount() != 1)
	{
		return false;
	}
	grFormSection.next();
	return grFormSection;
}
