sys_script_include.script:
function IsCodeRecord(vRecordOrSysId)
{
	var strSysId = '';
	if (IsValidSysId(vRecordOrSysId) == true)
	{
		strSysId = vRecordOrSysId;
	}
	else if (IsValidRecord(vRecordOrSysId))
	{
		strSysId = GetStringValue(vRecordOrSysId.sys_id);
	}
	else
	{
		return false;
	}

	var grMetadata = GetRecord('sys_metadata',vRecordOrSysId);
	if (IsValidRecord(grMetadata) == true)
	{
		return true;
	}
	var grPackage = GetRecord('sys_package',vRecordOrSysId);
	if (IsValidRecord(grPackage) == true)
	{
		return true;
	}


	var grTransporterRecord = new GlideRecord('x_snc_devtools_transporter_record');
	grTransporterRecord.addQuery('payload_sys_id','=',strSysId);
	grTransporterRecord.query();
	if (grTransporterRecord.next() == true)
	{
		return true;
	}
    return false;
}
